(()=>{let e="",n=[],t="",r="",o="",i="",a="",d="",m=!0;const c=document.getElementById("location"),u=document.getElementById("longitude"),l=document.getElementById("latitude"),g=document.getElementById("temperature"),L=document.getElementById("pressure"),h=document.getElementById("humidity"),p=document.getElementById("description"),T=document.getElementById("weatherIcon"),s=document.getElementById("locationSearch"),H=document.getElementById("searchLocationButton"),M=document.getElementById("change-temperature");H.addEventListener("click",(()=>{d=s.value,d=d.trim(),null!=d&&""!==d||(d="London"),console.log(d),fetch(`http://api.openweathermap.org/data/2.5/weather?q=${d}&appid=Insert_API_KeyHere`,{mode:"cors"}).then((e=>e.json())).then((d=>{e=d.name,n=[d.coord.lon,d.coord.lat],t=Math.round(Number(d.main.temp)-273),r=d.main.pressure,o=d.main.humidity,i=d.weather[0].description,a=d.weather[0].icon,c.innerHTML="Current Location: ",l.innerHTML="Latitude: ",u.innerHTML="Longitude: ",g.innerHTML="Average Temperature (C): ",L.innerHTML="Pressure (mbar): ",h.innerHTML="Humidity (percentage): ",p.innerHTML="Cloud Conditions: ",c.innerHTML+=e,l.innerHTML+=n[1],u.innerHTML+=n[0],g.innerHTML+=t,L.innerHTML+=r,h.innerHTML+=o,p.innerHTML+=i,T.src=`http://openweathermap.org/img/w/${a}.png`})).catch((e=>console.error(e)))})),M.addEventListener("click",(()=>{m?(m=!1,M.innerHTML="Change Temperature to Celsius",g.innerHTML=`Average Temperature (F): ${Math.round(9*t/5+32)}`):(m=!0,M.innerHTML="Change Temperature to Fahrenheit",g.innerHTML=`Average Temperature (C): ${t}`)}))})();